}

/** @internal */
export interface RunAsyncState<T extends AnimationTarget = any> {
  paused: boolean
  pauseQueue: Set<() => void>
  resumeQueue: Set<() => void>
  timeouts: Set<Timeout>
  delayed?: boolean
  asyncId?: number
  asyncTo?: AsyncTo<InferState<T>>
  promise?: AsyncResult<T>
