  results.length == 1
    ? results[0]
    : results.some(result => result.cancelled)
    ? getCancelledResult(target.get())
    : results.every(result => result.noop)
    ? getNoopResult(target.get())
    : getFinishedResult(
        target.get(),
        results.every(result => result.finished)
      )

